var arrayNodes = d3.values(nodes);
let resultNodes = arrayNodes.map(function(d) { return d.weight});

var nodeMin = d3.min(resultNodes);
var nodeMax = d3.max(resultNodes);

var colorScale = d3.scaleSequential()
        .domain([ nodeMin,nodeMax])
        .interpolator(d3.interpolateWarm);
        // .interpolate({colors: ["red", "blue"]}, {colors: ["white", "black"]});

nodesCircle.style( "fill", function(d) {
        return colorScale(d.weight);
   });



node.append("text")
    .attr ("dx", function(d) {
            return -(d.weight*1.5)})
    // .attr ("y", "-5em")
    .attr ("y",  function(d) {
            return -(d.weight*1.5)-15})

    .text(function (d) { return d.name })
    .attr("font-size", "20px");

node.append("text")
    .attr ("dx", -5)
    .attr ("dy", 0)
    .text(function (d) {
      if (d.weight>0 ){
        return d.weight
      }
    });

