// add the curvy lines
function tick() {
/*
    text.attr("x", d -> d.x )
    text.attr("y", d -> d.y )
*/
    path.attr("d", function(d) {
        var dx = d.target.x - d.source.x,
            dy = d.target.y - d.source.y,
            dr = Math.sqrt(dx * dx + dy * dy);
        return "M" +
            d.source.x + "," +
            d.source.y + "A" +
            dr + "," + dr + " 0 0,1 " +
            d.target.x + "," +
            d.target.y;
    });

    node
        .attr("transform", function(d) {
        return "translate(" + d.x + "," + d.y + ")"; })
};

// Pin the nodes
function dblclick(d){
console.log("Before")
console.log(d.fixed)

    if (d.fixed == true) {
         d3.select(this).classed ("fixed",d.fixed = false).style("fill",  function(d) {
            return colorScale(d.weight);
         });
         d.fx = null;
         d.fy = null;
         }

    else {d3.select(this).classed ("fixed",d.fixed = true).style("fill", "pink");

        d.fx = d.x;
        d.fy = d.y;
        }

    console.log("After")
    console.log(d.fixed)

};

// Creates the name cordonez top right
svg.append("g")
